import React from 'react'

const FilterUser = ({setUsers,state}) => {
  return (
    <div
      style={{
        width: "500px",
        display: "flex",
        justifyContent: "space-evenly",
        margin: "20px auto",
      }}
    >
      <div>
        <input
          type="radio"
          name="gender"
          value="all"
          defaultChecked
          onChange={() => {
            setUsers(state);
          }}
        />
        ALL
      </div>
      <div>
        <input
          type="radio"
          name="gender"
          value="male"
          onChange={() => {
            setUsers(state.filter((data) => data.gender === "male"));
          }}
        />
        MALE
      </div>
      <div>
        <input
          type="radio"
          name="gender"
          value="female"
          onChange={() => {
            setUsers(state.filter((data) => data.gender === "female"));
          }}
        />
        FEMALE
      </div>
    </div>
  );
}


export default FilterUser
